var bdageswiper = new Swiper("#ghbdages", {
  direction: "vertical",
  passiveListeners: true,
  spaceBetween: 30,
  loop: true,
  autoplay: {
    disableOnInteraction: true,
    delay: 1500
  },
  mousewheel: true,
  // 添加观察者，处理图片加载
  observer: true,
  observeParents: true,
  on: {
    init: function() {
      // 给所有徽标图片添加 no-lazy 类
      this.$el.find('img').addClass('no-lazy github-badge');
    }
  }
});

const ghbdage = document.getElementById("ghbdages");
if(ghbdage) {
  ghbdage.onmouseenter = function() {
    bdageswiper.autoplay.stop();
  };
  ghbdage.onmouseleave = function() {
    bdageswiper.autoplay.start();
  };
}